import {
  OptionsContainer,
  FiltersContainer,
  SelectedOptionsContainer,
  ClearAllFilters,
} from './EventConfirmationOptions.styles';
import { DateRangeFilter } from './Filters/DatePicker/DateRangeFilter';
import { EventTypeFilterDropdown } from './Filters/EventTypeFilterDropdown/EventTypeFilterDropdown';
import { EventConfirmationsSearch } from './Filters/EventConfirmationsSearch/EventConfirmationsSearch';
import { Pill } from '../../Pill/Pill';
import { useEventConfirmationOptions } from '../../../utils/hooks/useEventConfirmationOptions';
import { EventStatusFilterDropdown } from './Filters/EventStatusFilterDropdown/EventStatusFilterDropdown';
import { LumaIcon } from '@luma-fe-workspace/design-system';

// simple function to remove a value from an array by value
function removeItem<T>(arr: Array<T>, value: T): Array<T> {
  const index = arr.indexOf(value);
  if (index > -1) {
    arr.splice(index, 1);
  }
  return arr;
}

export const EventConfirmationOptions = () => {
  const { eventTypeFilter, eventStatusFilter, doFiltersExist, resetFilters } =
    useEventConfirmationOptions();

  return (
    <OptionsContainer>
      <FiltersContainer>
        <EventConfirmationsSearch />
        <DateRangeFilter />
        <EventTypeFilterDropdown />
        <EventStatusFilterDropdown />
      </FiltersContainer>
      {doFiltersExist && (
        <SelectedOptionsContainer>
          {eventTypeFilter.value.map((eventType: string, idx: number) => (
            <Pill
              text={eventType}
              icon="xmark"
              key={idx}
              onPillClick={() =>
                eventTypeFilter.setFilter(
                  removeItem<string>([...eventTypeFilter.value], eventType)
                )
              }
            />
          ))}
          {eventStatusFilter.value.map((eventStatus: string, idx: number) => (
            <Pill
              text={eventStatus}
              icon="xmark"
              key={idx}
              onPillClick={() =>
                eventStatusFilter.setFilter(
                  removeItem<string>([...eventStatusFilter.value], eventStatus)
                )
              }
            />
          ))}
          <ClearAllFilters onClick={() => resetFilters()}>
            <LumaIcon iconCode="xmark" />
            Clear All Filters
          </ClearAllFilters>
        </SelectedOptionsContainer>
      )}
    </OptionsContainer>
  );
};
