import {
  colors,
  LumaButton,
  LumaSingleDropDown,
  TextEntryField,
} from '@luma-fe-workspace/design-system';
import styled from 'styled-components';

export const ActionBar = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: space-between;
  width: 100%;
  height: 4rem;
  margin-bottom: 1.5rem;
  background-color: ${colors.neutral00white};
  border: 0.0625rem solid ${colors.neutral30};
  border-radius: 0.5rem;
  align-items: center;
  padding: 1rem;
  font-family: IBM Plex Sans;
`;
export const LeftActions = styled.div`
  display: flex;
  flex-direction: row;
`;

export const RightActions = styled.div``;

export const FilterButton = styled(LumaButton)`
  width: 6.5rem;
  font-family: IBM Plex Sans;
  font-size: 0.75rem;
  font-weight: 500;
`;

export const DateDropDown = styled(LumaSingleDropDown)`
  width: 8.25rem;
  margin-right: 0.75rem;
  cursor: pointer;
  input {
    font-family: IBM Plex Sans;
    font-size: 0.75rem;
    font-weight: 500;
  }
  span {
    font-family: IBM Plex Sans;
    font-size: 0.75rem;
  }
  ul {
    max-height: none;
  }
  & ${TextEntryField} {
    z-index: 0;
  }
`;
