import { SetStateAction, useEffect, useState } from 'react';
import { ConfirmationTypes, Statuses } from '../EventConfirmationModal';
import { ActionButtons } from './ButtonComponent.style';

interface Props {
  confirmationType: ConfirmationTypes | '';
  formsFilled: {
    allValuesFilled: boolean;
    atLeastOneValueFilled: boolean;
    noValuesFilled: boolean;
  };
  setEventStatus: React.Dispatch<SetStateAction<Statuses>>;
}

interface DisableButtons {
  firstButton: boolean;
  secondButton: boolean;
  thirdButton: boolean;
}

export const ButtonsComponent = ({
  confirmationType,
  formsFilled,
  setEventStatus,
}: Props) => {
  const [disableButtons, setDisableButtons] = useState<DisableButtons>({
    firstButton: false,
    secondButton: false,
    thirdButton: false,
  });
  const confirmations = {
    COUPON_PAYMENT: {
      secondButton: 'Missed',
      thirdButton: 'Confirmed',
    },
    ISSUER_CALL: {
      secondButton: 'Not Called',
      thirdButton: 'Called',
    },
    AUTO_CALL: {
      secondButton: 'Not Called',
      thirdButton: 'Called',
    },
    MATURITY: {
      secondButton: 'Not Matured',
      thirdButton: 'Matured',
    },
  };
  useEffect(() => {
    if (formsFilled.allValuesFilled === true) {
      setDisableButtons({
        firstButton: true,
        secondButton: true,
        thirdButton: false,
      });
    } else if (formsFilled.atLeastOneValueFilled === true) {
      setDisableButtons({
        firstButton: false,
        secondButton: true,
        thirdButton: true,
      });
    } else {
      setDisableButtons({
        firstButton: true,
        secondButton: false,
        thirdButton: true,
      });
    }
  }, [formsFilled]);

  const confirmationObject = confirmationType
    ? confirmations[confirmationType]
    : null;

  if (confirmationObject && Object.keys(confirmationObject).length > 0) {
    return (
      <>
        <ActionButtons
          variant={'secondary'}
          disabled={disableButtons.firstButton}
          size="medium"
          type="submit"
          onClick={() => setEventStatus(Statuses.REQUIRES_RESOLUTION)}
        >
          {'Review'}
        </ActionButtons>

        <ActionButtons
          variant={'secondary'}
          disabled={disableButtons.secondButton}
          size="medium"
          type="submit"
          onClick={() => setEventStatus(Statuses.NOT_CONFIRMED)}
        >
          {confirmationObject.secondButton}
        </ActionButtons>

        <ActionButtons
          variant={'primary'}
          disabled={disableButtons.thirdButton}
          size="medium"
          type="submit"
          onClick={() => setEventStatus(Statuses.CONFIRMED)}
        >
          {confirmationObject.thirdButton}
        </ActionButtons>
      </>
    );
  } else {
    return null;
  }
};
