import {
  LumaColor,
  breakpoints,
  colors,
  spacing,
} from '@luma-fe-workspace/design-system';
import styled from 'styled-components';

export const InfoWidgetWrapper = styled.div<{ styles?: string }>`
  display: flex;
  flex-direction: column;
  width: 100%;
  border: 0.0625rem solid ${LumaColor.NEUTRAL_40};
  border-radius: 0.5rem;
  overflow: hidden;
  background-color: ${colors.white};
  height: 22.8rem;

  @media ${breakpoints.tablet} {
    width: 100%;
  }
  ${(props) => props.styles}
`;

export const InfoWidgetTitle = styled.div`
  color: ${LumaColor.NEUTRAL_80};
  padding: 1rem 1rem 0.5rem 1rem;
  font-family: 'IBM Plex Sans', sans-serif;
  font-size: 1rem;
  font-style: normal;
  font-weight: 500;
  margin-bottom: ${spacing.xs};
`;

export const InfoWidgetContent = styled.div`
  height: 100%;
`;
