import { useMemo } from 'react';
import { useEventConfirmationOptions } from '../../../../../utils/hooks/useEventConfirmationOptions';
import { FilterDropdown, FilterDropdownOptions } from '../FilterDropdown';
import { formatEventStatus } from '../../../../../utils/gridUtils';

export const EventStatusFilterDropdown = () => {
  const { events, eventStatusFilter } = useEventConfirmationOptions();

  const eventStatuses = useMemo(
    () => [
      ...new Set(
        events.data.flatMap((event) => formatEventStatus(event)?.status || [])
      ),
    ],
    [events.data]
  );

  const options: FilterDropdownOptions<string>[] = useMemo(
    () =>
      eventStatuses.map((eventStatus) => ({
        label: eventStatus,
        value: eventStatus,
      })),
    [eventStatuses]
  );

  return (
    <FilterDropdown<string>
      options={options}
      label="Event Status"
      selected={eventStatusFilter.value}
      onChange={(typeValue) =>
        //Sadly this must be any for the time being as the correct MenuItem is not export from the design-system
        eventStatusFilter.setFilter(
          // eslint-disable-next-line @typescript-eslint/no-explicit-any
          typeValue.map((typeOption: any) => typeOption.value) || []
        )
      }
    />
  );
};
