import styled, { css } from 'styled-components';
import { colors, LumaColor } from '@luma-fe-workspace/design-system';

export const IssuerContainer = styled.div`
  display: flex;
  flex-direction: column;
  padding: 1.2rem 0;
  height: 100%;
  width: 100%;
  > * {
    display: flex;
    justify-content: start;
    height: 100%;
    width: 100%;
  }
  p {
    margin: 0rem;
    padding-top: 0.6875rem;
    font-size: 0.875rem;
    font-family: 'IBM Plex Sans', sans-serif;
    width: 100%;
    height: 100%;
  }
`;

export const IssuerLogo = styled.img<{ height?: string; width?: string }>`
  ${(props) => props.height && `height: ${props.height};`}
  ${(props) => props.width && `width: ${props.width};`}
`;

export const IssuerSecuritynumber = styled.p`
  font-weight: 500;
  color: ${colors.primary50};
`;

export const IssuerName = styled.p`
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
  display: block;
`;

export const SecurityText = styled.p`
  font-weight: bold;
  color: ${colors.neutral60};
`;

export const UnderlinerContainer = styled.div``;

export const BasketTypeText = styled.p`
  font-family: 'IBM Plex Sans', sans-serif;
  color: ${colors.neutral80};
  font-size: 0.875rem;
`;

export const UnderlierWrapper = styled.div`
  display: flex;
  flex-wrap: wrap;
  gap: 0.5rem;
  width: auto;
`;

export const UnderlierTicker = styled.div<{
  activeUnderlier?: boolean;
}>`
  display: flex;
  background-color: ${colors.neutral20};
  ${(props) => {
    if (props.activeUnderlier) {
      return css`
        background-color: ${LumaColor.NEUTRAL_40};
      `;
    } else {
      return css`
        background-color: ${LumaColor.NEUTRAL_20};
      `;
    }
  }}
  border-radius: 0.25rem;
  display: inline-block;
  height: 1.2rem;
  width: auto;
  &:last-child {
    margin-right: 0;
  }
`;

export const UnderlierText = styled.p`
  font-size: 0.688rem;
  padding: 0;
  margin: 0;
  text-align: center;
  text-transform: uppercase;
  padding: 0.6rem 0.5rem;
`;

export const StatusContainer = styled.div`
  width: 100%;
  display: flex;
  align-items: start;
  justify-content: center;
  flex-direction: column;
  padding: 0.25rem 0rem;
  p {
    font-size: 0.75rem;
    padding: 0rem;
    margin: 0.625rem 0rem;
  }
`;

export const StatusLabelContainer = styled.div`
  width: 100%;
  display: flex;
  gap: 1rem;
  flex-direction: row;
  align-items: center;
  justify-content: space-between;
`;
export const StatusLabel = styled.p<{ color?: string }>`
  color: ${(props) => props.color ?? 'black'};
  font-weight: 500;
`;
export const SettlementRates = styled.div`
  width: 100%;
`;
export const SettlementRate = styled.div`
  width: 100%;
  display: flex;
  gap: 1rem;
  flex-direction: row;
  justify-content: space-between;
`;
export const SettlementRateLabel = styled.p``;
export const SettlementRateValue = styled.p``;
export const StructuredContainer = styled.div`
  display: flex;
  white-space: pre-line;
  align-items: center;
  height: 6rem;
  max-width: 100%;
  overflow-wrap: break-word;
`;

export const StructuredText = styled.p`
  font-family: 'IBM Plex Sans', sans-serif;
  color: ${colors.neutral80};
  font-size: 0.875rem;
  line-height: 1;
  padding: 1rem 0.5rem;
`;

export const TooltipWrapper = styled.div`
  background-color: ${colors.neutral00white};
  border-radius: 8px;
  border: 1px solid red;
  border: solid 1px ${colors.neutral40};
  height: 100%;
  padding: 0.5rem;
  width: 100%;
`;
